mapper.select:
select

where f.name = #{name} and f.parent_id = #{parentId}
   limit 1

mapper.update:
update fs_struct_${owner}
set name      = #{name} ,
    parent_id = #{parentId}
where id = #{id}

mapper.delete:
delete
from fs_struct_${owner}
where parent_id = #{parentId}
  and name = #{name}

delete
from fs_struct_${owner}
where id in

    #{id}

delete from fs_struct_${owner} where id in ( WITH RECURSIVE tree AS ( SELECT id, name, parent_id, ARRAY [name] AS fpath FROM fs_struct_${owner} WHERE parent_id in
#{id}
UNION ALL SELECT si.id, si.name, si.parent_id, sp.fpath || si.name As fpath FROM fs_struct_${owner} As si JOIN tree AS sp ON (si.parent_id = sp.id) ) SELECT id FROM tree)

mapper.update:
update fs_struct_${owner}
set selected = #{selected}
where id = #{id}

update fs_struct_${owner}
set selected = exists(select 1 from fs_struct_${owner} where parent_id = #{dirId} selected = false)
where parent_id = #{dirId}

update fs_struct_${owner}
set selected = exists(select 1 from fs_struct_${owner} where found = true and selected = false)
where found = true

update fs_struct_${owner}
set selected = !selected
where id = #{id}
